import { createSlice } from "@reduxjs/toolkit";

const initialState = {cart: 0}

const cartSlice = createSlice({
    name: 'cart',
    initialState, 
    reducers: {
        addItem(state, action) {
            state.cart = state.cart + action.payload
        },
        increment(state) {
            state.cart++;
        },
        decrement(state) {
            state.cart--;
        }
    }
    // reducers: {
